mapper.select:
-- JdbcInfoDao.selectJdbcInfoCnt
SELECT  COUNT(*) CNT
FROM    TDACM00060 A
WHERE   A.CD_ID_HNM = #{CD_ID_HNM}
AND     A.CD        = #{CD}

-- JdbcInfoDao.selectJdbcInfoExtCnt
SELECT  COUNT(*) CNT
FROM    TDACM00061 A
WHERE   A.CD_ID_NM = #{CD_ID_NM}
AND     A.CD       = #{CD}

