mapper.insert:
-- TableDao.insertMetaTabCol
INSERT INTO TDACM00081
(
     DB_NM         
    ,OWNER         
    ,TAB_NM        
    ,COL_ID        
    ,COL_NM        
    ,COL_HNM       
    ,DATA_TYPE_DESC
    ,NULLABLE      
    ,PK            
    ,DATA_TYPE_NM  
    ,LEN           
    ,DECIMAL_CNT   
    ,COL_DESC      
    ,REG_DTM       
    ,REG_USR_ID    
    ,UPD_DTM       
    ,UPD_USR_ID    
) VALUES (
     #{DB_NM}
    ,#{OWNER}
    ,#{TAB_NM}
    ,CAST(#{COL_ID} AS INT)
    ,#{COL_NM}
    ,#{COL_HNM}
    ,#{DATA_TYPE_DESC}
    ,#{NULLABLE}
    ,#{PK}
    ,#{DATA_TYPE_NM}
    ,CASE WHEN #{LEN} IN ('',NULL) THEN 0 ELSE #{LEN} END
    ,CASE WHEN #{DECIMAL_CNT} IN ('',NULL) THEN 0 ELSE #{DECIMAL_CNT} END
    ,#{COL_DESC}
    ,curdate()
    ,#{REG_USR_ID}
    ,curdate()
    ,#{UPD_USR_ID}
)

mapper.select:
-- TableDao.selectMetaTabCnt
SELECT  COUNT(*) CNT
FROM    TDACM00071 A
WHERE   A.DB_NM   = #{DB_NM}
AND     A.OWNER   = #{OWNER}
AND     A.TAB_NM  = #{TAB_NM}

mapper.delete:
-- TableDao.deleteMetaTab
DELETE FROM TDACM00071
WHERE  DB_NM   = #{DB_NM}
AND    OWNER   = #{OWNER}
AND    TAB_NM  = #{TAB_NM}

