mapper.update:
-- TableDao.updateTabCol
UPDATE TDACM00010
SET  PKG2_NM      = #{PKG2_NM}
    ,PKG3_NM      = #{PKG3_NM}
    ,PKG4_NM      = #{PKG4_NM}
    ,PKG_HNM      = #{PKG_HNM}
    ,PKG_DESC     = #{PKG_DESC}
